public class Solution {

    // Time Limit Exceeded

    public static void main(String[] args) {
        Solution solution = new Solution();
//        String input = "aabbac";
        String input = "";
        long start = System.currentTimeMillis();
        String res = "";
        for (int i = 0; i < 1; i++) {
            res = solution.shortestPalindrome(input);
        }
        long finish = System.currentTimeMillis();
        System.out.println("res = " + res);
        System.out.println("time = " + (finish-start));
    }

    private int maxLen = 1;
    private int mStart = -1;
    private int mFinish = -1;

    public String shortestPalindrome(String s) {
        if (s.length() == 0) return "";
        if (s.length() == 1) return s;
        if (isPalindrome(s)) return s;

        //looking for a palindrome inside the string
        //mStart & mFinish are begin and end of the palindrome, if any
        char[] arr = s.toCharArray();
        for (int i = 0; i < arr.length; i++){
            check(arr,i,i);
            check(arr,i,i+1);
        }

        //if there is no any palindromes in the string
        //add all the letters of the string to begin of the string
        StringBuilder sb = new StringBuilder(s);
        if (mStart < 0 || mFinish < 0) {
            return createPalindrome(s);
        }else {
            //if a palindrome found in the string
            //check if is starts from first letter of the string
            //if yes, add letters from the end of the string
            String palindrome = s.substring(mStart, mFinish + 1);
            if (s.indexOf(palindrome) == 0) {
                for (int i = palindrome.length(); i < s.length(); i++){
                    sb.insert(0, s.charAt(i));
                }
            } else {
                //otherwise, revert the string and add to begin
                return createPalindrome(s);
            }
        }
        return sb.toString();

    }

    private void check(char[] arr, int start, int finish){
        if (start<0 || finish>=arr.length) return;
        if (arr[start] == arr[finish]){
            int len = finish-start+1;
            if (len > maxLen){
                maxLen = len;
                mStart = start;
                mFinish = finish;
            }
            check(arr, start-1, finish+1);
        }
    }

    private String createPalindrome(String s){
        StringBuilder sb = new StringBuilder(s);
        int i = s.length()-1;
        int pointer = 0;
        while (true){
            String backward = sb.reverse().toString();
            String forward = sb.reverse().toString();
            if (backward.equals(forward)) {
                break;
            }
            sb.insert(pointer++,sb.charAt(i));
        }
        return sb.toString();
    }

    private boolean isPalindrome(String s){
        StringBuilder sb = new StringBuilder(s);
        String backward = sb.reverse().toString();
        String forward = sb.reverse().toString();
        return backward.equals(forward);
    }

}
